function somme_sexa(e,t){var n=new RegExp("[ ,.:]","g"),l=e.split(n),a=t.split(n),r=parseInt(l[0]);if(2==l.length){r=parseInt(l[0],10);var o=parseInt(l[1],10)}else r=parseInt(l[0],10),o=parseInt("0");if(60<o)return"err:err";if(2==a.length)var u=parseInt(a[0],10),i=parseInt(a[1],10);else u=parseInt(a[0],10),i=parseInt("0");if(60<i)return"err:err";var g=o+i;if(59<g){g-=60;var m=1}else m=0;var s=m+r+u;if(g<10)var v=s+":0"+g;else v=s+":"+g;return v}function somme_tout(e){var t=document.getElementById("ligne_L_"+e).value,n=new RegExp("[ ,.:]","g"),l=t.split(n);t=2==l.length?2==l[1].length?l[0]+":"+l[1]:l[0]+":"+l[1]+"0":l[0]+":00",document.getElementById("ligne_L_"+e).value=t;var a=document.getElementById("ligne_L_0").value;for(i=1;i<16;i++)relev=document.getElementById("ligne_L_".concat(i)).value,a=somme_sexa(a,relev);document.getElementById("total_total").value=""+a}function transfert_total(){var e=document.getElementById("total_total").value,t=new RegExp("[ ,.:]","g"),n=e.split(t);document.getElementById("carnet_heure_de_vol").value=n[0]}function raz_page(){for(document.getElementById("ligne_L_0").value=document.getElementById("total_total").value,i=1;i<16;i++)document.getElementById("ligne_L_".concat(i)).value="00:00"}